package array_questions.Find_the_maximum_and_minimum_element_in_an_array;import java.util.Arrays;public class Main {    private static int[] findMaxMin(int[] arr) {        int max_element = Integer.MIN_VALUE;        int min_element = Integer.MAX_VALUE;        for(int i : arr){            if(i > max_element){                max_element = i;            }            if(i < min_element){                min_element = i;            }        }        return new int[]{min_element, max_element};    }    public static void main(String[] args) {        int arr[] = {5, 2, 6, 3, 7, 0};        System.out.println(Arrays.toString(findMaxMin(arr)));    }}