package array_questions.buy_sell_stock_each_day;public class Main {    private static int buySellStock(int[] stock) {        int profit = 0;        for(int i = 1; i < stock.length; i++){            if(stock[i] > stock[i-1]){                profit += stock[i] - stock[i-1];            }        }        return profit;    }    public static void main(String[] args) {        int stock[] = {7,1,5,3,6,4};        System.out.println(buySellStock(stock));    }}