package array_questions.dublicate_element_1_to_n;public class Main {    private static int findDuplicate(int[] arr) {        int n = arr.length;        for(int i = 0; i < arr.length; i++){            arr[arr[i] % n] = arr[arr[i] % n] + n;        }        for(int i = 0; i < n; i++){            if(arr[i] >= 2*n){                return i;            }        }        return -1;    }    public static void main(String[] args) {        int arr[] = {3, 1, 2, 4, 1};        System.out.println(findDuplicate(arr));    }}