package array_questions.leaders_in_an_array;import java.util.ArrayList;import java.util.Collections;import java.util.List;public class Main {    private static List<Integer> leader(int[] arr) {        int currentLeader = arr[arr.length-1];        List<Integer> list = new ArrayList<>();        list.add(currentLeader);        for(int i = arr.length-2; i >= 0; i--){            if(arr[i] >= currentLeader){                currentLeader = arr[i];                list.add(currentLeader);            }        }        return list;    }    public static void main(String[] args) {        int arr[] = {4, 10, 7, 2, 0, 5, 1};        List<Integer> list = leader(arr);        Collections.reverse(list);        for(int i : list){            System.out.print(i+" ");        }    }}