package array_questions.negative_one_side_of_array;import java.util.Arrays;public class Main {    private static void swapElement(int[] a, int x, int y) {        int temp = a[x];        a[x] = a[y];        a[y] = temp;    }    private static int[] negativeOneSide(int[] arr) {        int start = 0;        int current = 0;        while(current < arr.length){            if(arr[current] < 0){                swapElement(arr, start, current);                start++;            }            current++;        }        return arr;    }    public static void main(String[] args) {        int arr[] ={6, 1, 7, 2, -9, -3, 4, -1, 0, 5, -11};        System.out.println(Arrays.toString(negativeOneSide(arr)));    }}