package array_questions.reverse_each_word;import java.util.Stack;public class Main {    public String solve(String str){        String s[] = str.trim().split("\\s+");        Stack<String> stack = new Stack<>();        for(int i = 0; i < s.length; i++){            stack.push(s[i]);        }        String res = "";        while (!stack.empty()){            res = res + " " +stack.pop();        }        return res.trim();    }    public static void main(String[] args) {        String str = "   anil     kumar pandit";        System.out.println(new Main().solve(str));    }}