package array_questions.second_largest_number;public class Main {    private static int secondLargest(int[] arr) {        int n = arr.length;        int secondLarge = -1;        int largest_element = Integer.MIN_VALUE;        for(int i = 0; i < n; i++){            if(arr[i] > largest_element){                secondLarge = largest_element;                largest_element = arr[i];            }            if(arr[i] != largest_element && arr[i] > secondLarge){                secondLarge = arr[i];            }        }        return secondLarge;    }    public static void main(String[] args) {        int arr[] = {3, 2, 7, 1, 14, 0};        System.out.println(secondLargest(arr));    }}