package array_questions.stock_span_problem;import java.util.Arrays;import static java.lang.Math.max;public class Main {    public static int[] solve(int[] stock) {        int max_count = Integer.MIN_VALUE;        int s[] = new int[stock.length];        s[0] = 1;        for(int i = 1; i < stock.length; i++){            s[i] = 0;            for(int j = i; j >= 0; j--){                if(stock[i] >= stock[j])                    s[i]++;            }        }        return s;    }    public static void main(String[] args) {        int stock[] = {100, 80, 60, 70, 60, 75, 85};        System.out.println(Arrays.toString(solve(stock)));    }}