package array_questions.validate_parenthesis;import java.util.Stack;public class Main {    public Boolean solve(String str){        Stack<Character> stack = new Stack<>();        for(char ch : str.toCharArray()){            if(ch == '[' || ch == '{' || ch == '(')                stack.push(ch);            else if (ch == ']' || ch == '}' || ch == ')'){                if(stack.empty()){                    return false;                }                int top = stack.pop();                if (ch == ']' && top != '[' ||                        ch == '}' && top != '{' ||                        ch == ')' && top != '('){                    return false;                }            }        }        return true;    }    public static void main(String[] args) {    }}