package binary_search_questions.find_ceil_of_element;public class Main {    private static int findCeil(int[] arr, int element) {        int start = 0, end = arr.length-1;        int res  = -1;        while(start <= end){            int mid = start + (end-start)/2;            if(arr[mid] == element)                return arr[mid];            else if(element < arr[mid]){                res = arr[mid];                end = mid - 1;            }            else                start = mid + 1;        }        return res;    }    public static void main(String[] args) {        int arr[] = {2, 5, 8, 12, 45, 67, 78};        System.out.println(findCeil(arr, 9));    }}