package binary_search_questions.find_the_min_element_in_rotated_sorted_array;public class Main {    private static int findMinElement(int[] arr) {        int start = 0, end = arr.length-1;        int N = arr.length;        while(start <= end){            int mid = start + (end - start)/2;            if(arr[mid] < arr[(mid-1+N) % N] && arr[mid] < arr[(mid+1) % N]){                return mid;            }            else if (arr[start] < arr[mid])                start = mid+1;            else                end = mid-1;        }        return -1;    }    public static void main(String[] args) {        int arr[] = {7, 8, 1, 2, 3, 4, 5, 6};        System.out.println(findMinElement(arr));    }}