package binary_search_questions.minimum_abs_diff_in_sorted_array;public class Main {    private static int findAbsDiff(int[] arr, int key) {        int start = 0, end = arr.length-1;        while(start <= end){            int mid = start + (end- start)/2;            if(key == arr[mid]){                return 0;            }            else if(key > arr[mid])                start = mid + 1;            else                end = mid - 1;        }        return (Math.abs(key-arr[start]) < Math.abs(key-arr[end])) ? Math.abs(key-arr[start]) : Math.abs(key-arr[end]);    }    public static void main(String[] args) {        int arr[] = {2, 3, 5, 10 , 15};        int key = 7;        if(arr[0] > key)            System.out.println(Math.abs(key-arr[0]));        else if(arr[arr.length-1] < key)            System.out.println(Math.abs(key-arr[arr.length-1]));        else            System.out.println(findAbsDiff(arr, key));    }}