package binary_search_questions.next_letter;public class Main {    private static char findNextLetter(char[] letter, char key) {        int start = 0, end = letter.length-1;        char res = ' ';        while(start <= end){            int mid = start + (end-start)/2;            if(key < letter[mid]){                res = letter[mid];                end = mid - 1;            }            else                start = mid + 1;        }        return res;    }    public static void main(String[] args) {        char letter[] = {'a', 'c', 'f', 'h'};        char key = 'i';        System.out.println(findNextLetter(letter, key));    }}