package binary_search_questions.search_sorted_array;public class Main {    private static int searchNumber(int[] arr, int element) {        int start = 0, end = arr.length-1;        while(start <= end){            int mid = start + (end-start)/2;            if(arr[mid] == element)                return mid;            else if (element < arr[mid]) {                end = mid - 1;            }            else                start = mid + 1;        }        return -1;    }    public static void main(String[] args) {        int arr[] = {1, 2, 3, 4, 5, 6, 7};        System.out.println(searchNumber(arr, 6));    }}