package sliding_window_questions.find_first_neg_number_in_every_window;import java.util.ArrayList;import java.util.List;public class Main {    private static List<Integer> findFirstNegFromAllWindow(int[] arr, int windowSize) {        List<Integer> result = new ArrayList<>();        List<Integer> list = new ArrayList<>();        int i = 0, j = 0;        while(j < arr.length){            if(arr[j] < 0){                list.add(arr[j]);            }            if(j-i+1 < windowSize){                j++;            }            else if (j-i+1 == windowSize){                if(list.isEmpty())                    result.add(0);                else {                    result.add(list.get(0));                    if(list.get(0) == arr[i])                        list.remove(0);                }                j++;                i++;            }        }        return result;    }    public static void main(String[] args) {        int arr[] = {12, -1, -7, 8, -15, 30, 16, 28};        int window_size = 3;        List<Integer> list = findFirstNegFromAllWindow(arr, window_size);        list.stream().forEach(i -> {            System.out.println(i);        });    }}