package sliding_window_questions.find_largest_num_of_all_the_subarray_of_size_k;import java.util.ArrayList;import java.util.List;import static java.lang.Math.max;public class Main {    private static List<Integer> findLargestNumberInKSize(int arr[], int window_size) {        int i = 0, j = 0;        int max_element = Integer.MIN_VALUE;        List<Integer> list = new ArrayList<>();        while(j < arr.length){            max_element = max(max_element, arr[j]);            if(j-i+1 < window_size){                j++;            }            else if (j-i+1 == window_size){                list.add(max_element);                if(arr[i] == max_element)                    max_element = Integer.MIN_VALUE;                i++;                j++;            }        }        return list;    }    public static void main(String[] args) {        int arr[] = {1, 13, -1, -3, 5, 3, 6, 7};        int window_size = 3;        for(int  i : findLargestNumberInKSize(arr, window_size)){            System.out.println(i);        }    }}