package sliding_window_questions.find_max_sum_of_size_k;import static java.lang.Math.max;public class Main {    private static int maxSubarraySum(int[] arr, int windowSize) {        int i = 0, j = 0;        int sum = 0;        int max_sum = Integer.MIN_VALUE;        while(j < arr.length){            sum += arr[j];            if(j-i+1 < windowSize){                j++;            }            else if (j-i+1 == windowSize){                max_sum = max(max_sum, sum);                sum = sum - arr[i];                i++;                j++;            }        }        return max_sum;    }    public static void main(String[] args) {        int arr[] = {3, 1, 5, 2, 7, 8, 4, 2};        int window_size = 3;        System.out.println(maxSubarraySum(arr, window_size));    }}