package sliding_window_questions.find_min_sum_of_size_k;import static java.lang.Math.min;public class Main {    private static int minSubarraySum(int[] arr, int windowSize) {        int i = 0, j = 0;        int sum = 0;        int min_sum = Integer.MAX_VALUE;        while(j < arr.length){            sum = sum + arr[j];            if(j-i+1 < windowSize)                j++;            else if(j-i+1 == windowSize){                min_sum = min(min_sum, sum);                sum = sum - arr[i];                i++;                j++;            }        }        return min_sum;    }    public static void main(String[] args) {        int arr[] = {3, 1, 5, 2, 7, 8, 4, 2};        int window_size = 3;        System.out.println(minSubarraySum(arr, window_size));    }}