package sliding_window_questions.find_the_largest_window_of_sum_k;import static java.lang.Math.max;public class Main {    private static int findLargestWindow(int[] arr, int k) {        int i = 0, j = 0;        int sum = 0;        int max_window = 0;        while(j < arr.length){            sum = sum + arr[j];            if(sum < k)                j++;            else if (sum == k){                max_window = max(max_window, j-i+1);                j++;            }            else if (sum > k){                while (sum > k){                    sum = sum - arr[i];                    i++;                }                j++;            }        }        return max_window;    }    public static void main(String[] args) {        int arr[] = {4, 1, 1, 1, 2, 3, 5};        int K = 5;        System.out.println(findLargestWindow(arr, K));    }}