package sliding_window_questions.num_of_occurances_of_given_str;public class Main {    private static boolean anagram(String s, String ptr) {        int freq[] = new int[256];        if(s.length() != ptr.length())            return false;        for(int i = 0; i < s.length(); i++){            freq[s.charAt(i)-'a']++;            freq[ptr.charAt(i)-'a']--;        }        for(int i = 0; i < 256; i++){            if(freq[i] != 0)                return false;        }        return true;    }    private static int occurances(String str, String ptr) {        int i = 0, j = 0;        String s = "";        int count = 0;        while(j < str.length()){            s = s + str.charAt(j);            if(j-i+1 < ptr.length()){                j++;            }            else if (j-i+1 == ptr.length()){                if(anagram(s, ptr)){                    count++;                }                s = s.substring(1);                j++;                i++;            }        }        return count;    }    public static void main(String[] args) {        String str = "ofrxdofrooxfofr";        String ptr = "for";        System.out.println(occurances(str, ptr));    }}