package stack_questions.next_largest_element;import java.util.ArrayList;import java.util.List;import java.util.Stack;public class Main {    private static List<Integer> nextLargest(int[] arr) {        Stack<Integer> stack = new Stack<>();        List<Integer> res = new ArrayList<>();        for(int i = arr.length-1; i >= 0; i--){            if(!stack.empty()){                if(stack.peek() > arr[i])                    res.add(stack.pop());                else                    stack.pop();            }            else {                    res.add(arr[i]);            }        }        return res;    }    public static void main(String[] args) {        int arr[] = {1, 3, 2, 4};        List<Integer> list = nextLargest(arr);        list.stream().forEach(System.out::println);    }}